import java.util.Scanner;

// 
public class Main {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner input = new Scanner(System.in);
		String checkString = input.next();
		
		System.out.println("checkString = " + checkString);
		System.out.println("largestProduct = " + largestProduct(checkString, 13));
	}
	
	public static long largestProduct(String checkString, int consecutiveNumbers){
		long tempProduct = 1, currentLargestProduct = 0;
		String currentLargest = "";
		
		for(int n = 0; n < checkString.length()-consecutiveNumbers; n++){
			// take out "consecutiveNumbers" consecutiveNumbers and calculate their product, if it is larger
			// than the current then set this as the currentLargest
			String tempString = checkString.substring(n, n+consecutiveNumbers);
			for(int i = 0; i < tempString.length(); i++){
				tempProduct *= tempString.charAt(i) - 48;
			}
			
			if(tempProduct > currentLargestProduct){
				currentLargest = tempString;
				currentLargestProduct = tempProduct;
			}
			tempProduct = 1;
		}
		
		System.out.println("the largest product has the following string " + currentLargest);
		return currentLargestProduct;
	}

}
